import React, { FunctionComponent, useState } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import Alert from '@material-ui/lab/Alert';
import CSS from 'csstype';
import {signin} from '../services/http-requests'


export const LoginPage: FunctionComponent<{   setName: Function }> = ({setName})=> {

  function loginSubmit(){
    signin(username)
    .then(response => {
      setName(username)
        setRedirect(true)
    })
    .catch(e => {
      if(e.response && e.response.status == 401){
        console.log("eeeeeeeeeeeeeeeeeeeeeeeeeeeeeee");

        setError(true)
      }

      console.log(e);
    });
    console.log("Submit");
  }

  const [username, setUsername] = useState("");
  const [redirect, setRedirect] = useState(false);
  const [error, setError] = useState(false);
  const [open, setOpen] = useState(false);


  return <>
  <div style={containerStyle}>
   <div style={boxStyle}>
    <h3>Login</h3>
      <TextField
          id="filled-full-width"
          label="Username"
          placeholder="Your name"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          variant="filled"
          onChange={element =>setUsername(element.target.value)}
          value={username}
        />
      <br/>
      {error?
        <div><Alert severity="error">You have not an account!</Alert></div>
        :null
      }
      <div style={buttonStyle}>
        <Button variant="contained" color="primary" onClick={loginSubmit}>
          sign in
        </Button>
      </div>
    </div>
  </div>
  </>
}



const containerStyle: CSS.Properties = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};
const h1Styles: CSS.Properties = {
  backgroundColor: 'rgba(255, 255, 255, 0.85)',
  position: 'absolute',
  right: 0,
  bottom: '2rem',
  padding: '0.5rem',
  fontFamily: 'sans-serif',
  fontSize: '1.5rem',
  boxShadow: '0 0 10px rgba(0, 0, 0, 0.3)'
};
const boxStyle: CSS.Properties = {
  marginTop: '50px',
  width: '500px',
  height: '250px',
  paddingTop: '60px',
  borderColor: 'black',
  borderWidth: '2px',
  borderStyle: 'solid'
};
const buttonStyle: CSS.Properties = {
  float: 'right'
};
