import React, { FunctionComponent, useEffect, useState } from 'react';
import CSS from 'csstype';
import {get_library} from '../services/http-requests'
import { Container , Row, Col } from 'react-bootstrap';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import { Theme, createStyles, makeStyles } from '@material-ui/core/styles';
import {Video} from '../components/video'
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      backgroundColor: 'red',
    },
    gridList: {
      height: '680px',
      backgroundColor: 'beige',
      transform: 'translateZ(0)',
    },
    gridListTile: {
      // marginBlockStart: '1px',
      backgroundColor: 'bisque',
      borderColor: 'black',
      borderWidth: '2px',
      borderStyle: 'solid'
    },
  }),
);

type Video = {
  title: string,
  id: string,
  name: string
}

export const LibraryPage: FunctionComponent<{ initial?: string }> = ({ initial = "" })=> {

  const [videos, setVideos] = useState<Array<Video>>([
    // {title: "strdding",id: "strindg",name: "string"},
    // {title: "string",id: "strieng",name: "string"},
    // {title: "string",id: "stdrieng",name: "string"},
    // {title: "string",id: "stdrienlg",name: "string"},
    // {title: "string",id: "strideng",name: "string"},
    // {title: "stridng",id: "sstrideng",name: "string"},
    // {title: "string",id: "ssdtrideng",name: "string"},
    // {title: "stridng",id: "sdstrideng",name: "string"},
    // {title: "string",id: "strizng",name: "string"}
  ]);
  const classes = useStyles();

  useEffect(() => {
    get_library()
    .then(response => {
      let data = response.data;
      setVideos(data['data'])
    })
    .catch((e) => {
      console.log(e);
    });
  },[]);

  function startPlay(id: string){
    console.log(id);


  }


  return <>
  <div style={containerStyle}>
    <div style={boxStyle}>
      <Container>
        <Row style={containers}>
          <Col style={videosContainer} xs={6} md={4}>
            <h2>Your library</h2>
            <Button variant="contained"  >
                <IconButton aria-label={`info about `} >
                  <InfoIcon />
                </IconButton>
            </Button>
            <GridList cellHeight={140} className={classes.gridList} cols={1}>
              {videos.map((v) => (
                <GridListTile className={classes.gridListTile} key={v.id} cols={1}>
                  <Video title={v.title} id={v.id} />
                  <GridListTileBar
              title={
                <Button
                  variant="contained"
                  color="default"
                  // className={classes.button}
                  startIcon={<InfoIcon />}
                >
                  Play
                </Button>
              }
              actionIcon={
                <IconButton aria-label={`info about `} >
                  <InfoIcon />
                </IconButton>
              }
            />
                </GridListTile>
              ))}
            </GridList>
          </Col>
          <Col style={embededVideoContainer} xs={12} md={8}>
            <div style={embededVideo}>

            </div>
          </Col>
        </Row>
      </Container>
    </div>
  </div>
  </>
}



const containerStyle: CSS.Properties = {
  display: 'flex',
  justifyContent: 'center',
};

const boxStyle: CSS.Properties = {
  justifyContent: 'start',
  marginTop: '50px',
  width: '80%',
  height: '800px',
  paddingTop: '60px',
  borderColor: 'black',
  borderWidth: '2px',
  borderStyle: 'solid'
};
const containers: CSS.Properties = {
  // height: '690px',
  flex: 'flex-start',
  backgroundColor: 'beige'

};
const videosContainer: CSS.Properties = {
  // backgroundColor: 'blue',

};
const embededVideoContainer: CSS.Properties = {
  backgroundColor: 'red'
};
const embededVideo: CSS.Properties = {
  backgroundColor: 'blue',
  borderWidth: '1px',
  width: '300',
  height: '500'

};
const gridList:CSS.Properties ={
  flexWrap: 'nowrap',
  // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
  transform: 'translateZ(0)',
}
const root: CSS.Properties={
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'space-around',
  overflow: 'hidden',
}
