import { FunctionComponent, useEffect, useState } from 'react';
import CSS from 'csstype';
import { Container , Row, Col } from 'react-bootstrap';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SearchIcon from '@material-ui/icons/Search';
import CancelIcon from '@material-ui/icons/Cancel';
import { Theme, createStyles, makeStyles } from '@material-ui/core/styles';
import {get_library,add_video, delete_video} from '../services/http-requests'
import {Video} from '../components/video'
import {VideoPlayer} from '../components/videoplayer'
import {Suggestion} from '../components/suggestion'
import type { SearchObj} from '../types'

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      backgroundColor: 'red',
    },
    gridList: {
      height: '680px',
      backgroundColor: 'beige',
      transform: 'translateZ(0)',
    },
    gridListTile: {
      backgroundColor: 'bisque',
      borderColor: 'black',
      borderWidth: '2px',
      borderStyle: 'solid'
    },
  }),
);

export type Video = {
  title: string,
  id: string,
}

export const LibraryPage: FunctionComponent<{ initial?: string }> = ({ initial = "" })=> {

  const [videos, setVideos] = useState<Array<Video>>([]);

  const [videoToPlay, setVideoToPlay] = useState<Video>({title:"", id:""})
  const [displaySearch, setDisplaySearch] = useState(false)
  const [searchedObj, setSearchedObj] = useState<SearchObj>({title:"", n:0})
  console.log(searchedObj);

  const classes = useStyles();

  useEffect(() => {
    getLibrary()

  },[]);

  function getLibrary() {
    get_library()
    .then(response => {
      let data = response.data;
      setVideos(data['data'])
    })
    .catch((e) => {
      console.log(e);
    });
  }

  function addVideo(title:string, id:string){
   console.log("jjj ",title);
   console.log("jjj ",id);
   add_video({title:title, id:id})
   .then(response => {
      getLibrary()
  })
  .catch((e) => {
    console.log(e);
  });
  }

  function removeVideo(id:string){
    delete_video(id)
   .then(response => {
      getLibrary()
  })
  .catch((e) => {
    console.log(e);
  });
  }

  function startPlay(v :Video){
    setVideoToPlay(v)
    setDisplaySearch(false)
  }
  function openSearch(){
    setDisplaySearch(true)
  }
  function closeSearch(){
    setDisplaySearch(false)
  }

  function controlButtons(){
    if(!displaySearch)
     { console.log("bbbbbbbbb");

       return (
        <Button
          variant="contained"
          color="default"
          // className={classes.button}
          startIcon={<SearchIcon />}
          onClick= {openSearch}
          >Search
        </Button>
      )}
    else
      return (
        <Button
          variant="contained"
          color="default"
          // className={classes.button}
          startIcon={<CancelIcon />}
          onClick= {closeSearch}
        >Stop searching
        </Button>
      )
  }

  return <>
  <div style={containerStyle}>
      <Container>
        <Row style={containers}>
          <Col style={videosContainer} xs={6} md={4}>
            <h2>Your library</h2>
            {controlButtons()}
            <GridList cellHeight={140} className={classes.gridList} cols={1}>
              {videos.map((v) => (
                <GridListTile className={classes.gridListTile} key={v.id} cols={1}>
                  <Video title={v.title} id={v.id} />
                    <GridListTileBar
                    title={
                      <Button
                        variant="contained"
                        color="default"
                        // className={classes.button}
                        startIcon={<PlayArrowIcon />}
                        onClick= {()=>startPlay(v)}
                      >
                        Play
                      </Button>
                    }
                    actionIcon={
                      <IconButton onClick={()=>removeVideo(v.id)} >
                        <DeleteIcon />
                      </IconButton>
                    }
                  />
                </GridListTile>
              ))}
            </GridList>
          </Col>
          <Col style={embededVideoContainer} xs={12} md={8}>
            {!displaySearch?
              <div style={embededVideo}>
                <VideoPlayer title={videoToPlay.title} id={videoToPlay.id}/>
              </div>
            :
            <div>

              <Suggestion addVideo={addVideo}/>
            </div>
            }
          </Col>
        </Row>
      </Container>
    </div>
  </>
}



const containerStyle: CSS.Properties = {
  display: 'flex',
  alignItems: 'stretch',
  justifyContent: 'stretch'
};

const boxStyle: CSS.Properties = {
  justifyContent: 'start',
  marginTop: '20px',
  width: '85%',
  height: '800px',
  paddingTop: '60px',
  borderColor: 'black',
  borderWidth: '2px',
  borderStyle: 'solid'
};
const containers: CSS.Properties = {
  // height: '690px',
  backgroundColor: 'beige'

};
const videosContainer: CSS.Properties = {
  // backgroundColor: 'blue',

};
const embededVideoContainer: CSS.Properties = {
  backgroundColor: 'red',
  // justifyContent: 'center',
  // alignItems: 'center',
  display: 'flex',
  flexDirection: 'column'
};
const embededVideo: CSS.Properties = {
  backgroundColor: 'blue',
  borderWidth: '1px',
  width: '100%',
  height: '70%',
  justifyContent: 'center',
  alignSelf: 'center',
  alignItems: 'center',
  marginTop: '30px',
  display: 'flex'

};
const gridList:CSS.Properties ={
  flexWrap: 'nowrap',
  // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
  transform: 'translateZ(0)',
}
const root: CSS.Properties={
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'space-around',
  overflow: 'hidden',
}
